mapper.select:
select count(*)
from tbl_clue as c
join tbl_user as u
on c.owner = u.id



        c.fullname like '%' #{name} '%'




        and u.name like '%' #{owner} '%'




        and c.company > #{startDate}




        and c.phone < #{endDate}




        and c.source < #{endDate}




        and c.myphone < #{endDate}




        and c.state < #{endDate}

select
c.id, c.fullname, c.appellation, u.name as owner, c.company, c.job, c.email, c.phone, c.website, c.mphone, c.state, c.source, c.createBy
from tbl_clue as c join tbl_user as u on c.owner = u.id
u.name like '%' #{owner} '%'
and c.fullname like '%' #{fullname} '%'
and c.company > #{company}
and c.phone < #{phone}
and c.source < #{source}
and c.mphone < #{mphone}
and c.state < #{state}
order by c.createTime desc limit #{skipCount},#{pageSize}
